INSERT INTO `clchangelog` (`id`, `date`, `type`, `notes`) VALUES
(1, '25.3.2015', 'Background', 'Adding of possibility to set for each side of background different clickTag value'),
(2, '13.5.2015', 'ilayer', 'Adding of possibility to use image source file and own closebutton.'),
(3, '13.1.2016', 'All formats', 'Revision including adding of HTML 5 alternatives to formats, where it is possible'),
(4, '18.1.2017', 'Adding of new formats', 'Corner, Drawbridge, Double layered background'),
(5, '18.1.2017', 'Adding of new format', 'Background sidekick'),
(6, '16.3.2017', 'Main creative library version increased', 'from 1.02.37 to version 1.02.38, due to upgrade of sidekick format to support HTML 5 creatives.'),
(7, '16.3.2017', 'Adding of new format', 'Sidekick, which supports HTML5 creatives'),
(8, '12.4.2017', 'Adding of new formats', 'Expand and Pushdown, which supports HTML5 creatives'),
(9, '4.8.2017', 'Corner script modification', 'Changed fixed HTTP protocol in script part on relative procol // due to conflict on SSL pages'),
(10, '6.11.2017', 'Adding of new formats', 'Added Standard video banner, Videostitial, Video back layer and Floor ad video scripts, which work on HTML videoplayer basis now.'),
(11, '15.11.2017', 'Modification of scripts', 'Added possibility to set offset for closebutton in case of Drawbridge format.'),
(12, '16.11.2017', 'Modification of scripts', 'Added possibility to set background for custom element in case of Background image format.'),
(13, '16.11.2017', 'Modification of scripts', 'Added possibility to set color and background for custom element in case of Double layered background format.'),
(14, '13.02.2018', 'Bugfix', 'Fixed bug in case of displaying of video formats in Internet Explorer/Edge browsers.'),
(15, '13.02.2018', 'Modification of scripts', 'Added possibility to set custom HTML in case of Standard Video format.'),
(16, '28.05.2018', 'Modification of scripts', 'Adding of new function related to videobanner format if sound is muted.'),
(17, '07.08.2018', 'Modification of scripts', 'Added possibility to set HTML starter in case of Background sidekick format.'),
(18, '28.08.2018', 'Adding of new format', 'Video megaboard'),
(19, '01.02.2019', 'Adding of new format', 'Interscroller'),
(20, '01.02.2019', 'Update of library', 'Increased version of main creative library on 1.3.7');

